import React from 'react';
import { Icon, Label, Menu, Table, Modal} from "semantic-ui-react";
import 'semantic-ui-css/semantic.min.css'

class ProductTable extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            products: [],
            modalIsOpen: false
        }
        this.openModal = this.openModal.bind(this);
        this.closeModal = this.closeModal.bind(this);

    }

    openModal(product) {
        this.setState ({
           modalIsOpen: true
        });
    }

    closeModal() {
        this.setState({
            modalIsOpen: false
        });
    }

    componentDidMount() {
        fetch('/api/v1/products')
            .then(res => res.json())
            .then(data => this.setState({ products: data }));

    }


    render() {
        return (
            <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Id</Table.HeaderCell>
                        <Table.HeaderCell>Sku</Table.HeaderCell>
                        <Table.HeaderCell>Name</Table.HeaderCell>
                        <Table.HeaderCell>Brand</Table.HeaderCell>
                        <Table.HeaderCell>Currentstock</Table.HeaderCell>
                        <Table.HeaderCell>Thresholdstock</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                    {this.state.products.map(product =>
                        <Table.Row key={product}>
                            <Table.Cell>{product[0]}</Table.Cell>
                            <Table.Cell>{product[1]}</Table.Cell>
                            <Table.Cell>{product[2]}<span style={{float:"right"}}><a onClick={()=>this.openModal(product)}>PURCHASE </a>| <a >SHIP ORDER </a></span></Table.Cell>
                            <Table.Cell>{product[3]}</Table.Cell>
                            <Table.Cell>{product[4]}</Table.Cell>
                            <Table.Cell>{product[5]}</Table.Cell>
                        </Table.Row>
                    )}

                </Table.Body>

                <Modal
                    closeIcon
                    open={open}
                    trigger={<Button>Show Modal</Button>}
                    onClose={() => setOpen(false)}
                    onOpen={() => setOpen(true)}
                >
                    <Header icon='archive' content='Archive Old Messages' />
                    <Modal.Content>
                        <p>
                            Your inbox is getting full, would you like us to enable automatic
                            archiving of old messages?
                        </p>
                    </Modal.Content>
                    <Modal.Actions>
                        <Button color='red' onClick={() => setOpen(false)}>
                            <Icon name='remove' /> No
                        </Button>
                        <Button color='green' onClick={() => setOpen(false)}>
                            <Icon name='checkmark' /> Yes
                        </Button>
                    </Modal.Actions>
                </Modal>

            </Table>


        )
    }
}

export default ProductTable;
