import React from 'react';
import {Table, Modal, Header, Form, Select} from "semantic-ui-react";
import 'semantic-ui-css/semantic.min.css'

class ProductTable extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            products: [],
            modalIsOpen: false,
            id:'',
            sku:'',
            name:'',
            currentStock:'',
            suppliers: [],
            supplierOptions:[]
        }
        this.openModal = this.openModal.bind(this);
        this.closeModal = this.closeModal.bind(this);

    }

    openModal(product) {
        this.setState ({
           modalIsOpen: true,
           name: product[2],
           id: product[0],
           sku: product[1],
           currentStock: product[4]
        });
        this.prepareOptions();
        console.log(this.state.supplierOptions);
    }

    closeModal() {
        this.setState({
            modalIsOpen: false
        });
    }

    prepareOptions() {
       for(var i = 0; i < this.state.suppliers.length; i++) {
           this.state.supplierOptions.push({key:this.state.suppliers[i][0],text:this.state.suppliers[i][1],value:this.state.suppliers[i][0]});
       }

    }

    componentDidMount() {
        fetch('/api/v1/products')
            .then(res => res.json())
            .then(data => this.setState({ products: data }));

        fetch('/api/v1/suppliers')
            .then(res => res.json())
            .then(data => this.setState({suppliers: data}));


    }


    render() {
        return (
            <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Id</Table.HeaderCell>
                        <Table.HeaderCell>Sku</Table.HeaderCell>
                        <Table.HeaderCell>Name</Table.HeaderCell>
                        <Table.HeaderCell>Brand</Table.HeaderCell>
                        <Table.HeaderCell>Currentstock</Table.HeaderCell>
                        <Table.HeaderCell>Thresholdstock</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                    {this.state.products.map(product =>
                        <Table.Row key={product[0]}>
                            <Table.Cell>{product[0]}</Table.Cell>
                            <Table.Cell>{product[1]}</Table.Cell>
                            <Table.Cell>{product[2]}<span style={{float:"right"}}><a onClick={()=>this.openModal(product)}>PURCHASE </a>| <a >SHIP ORDER </a></span></Table.Cell>
                            <Table.Cell>{product[3]}</Table.Cell>
                            <Table.Cell>{product[4]}</Table.Cell>
                            <Table.Cell>{product[5]}</Table.Cell>
                        </Table.Row>
                    )}

                </Table.Body>

                <Modal
                    closeIcon
                    open={this.state.modalIsOpen}
                    onClose={() => this.closeModal()}
                    onOpen={() => this.openModal()}
                >
                    <Header icon='archive' content='Make Purchase' />
                    <Modal.Content>
                        <Form onSubmit={this.makePurchase} method="POST">
                         <Form.Group widths='equal'>
                          <Form.Input
                            fluid
                            disabled
                            label='Product Id'
                            value={this.state.id}
                          />

                            <Form.Input
                                fluid
                                disabled
                                label='Product SKU'
                                value={this.state.sku}
                            />

                            <Form.Input
                                fluid
                                disabled
                                label='Product Name'
                                value={this.state.name}
                            />

                            <Form.Input
                                fluid
                                disabled
                                label='Product Current Qty'
                                value={this.state.currentStock}
                            />

                           </Form.Group>
                            <Form.Group widths='equal'>
                                <Form.Input
                                  fluid
                                  placeholder='0'
                                  label='Quantity Required'
                                />

                             <Form.Field
                                fluid
                                control = {Select}
                                label='Select Supplier'
                                placeholder='Generic Supplier'
                                options={this.state.supplierOptions}
                             />

                            </Form.Group>
                           <Form.Button className="btn btn-uth-submit"> Purchase </Form.Button>

                        </Form>

                    </Modal.Content>
                    <Modal.Actions>

                    </Modal.Actions>
                </Modal>

            </Table>


        )
    }
}

export default ProductTable;
